import React, { Component } from 'react';
import ApiService from '../ApiService';

import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';


class AddUser extends Component{
    constructor(props){
        super(props);

        this.state ={
            userName: '',
            password: '',
            fristName: '',
            lastName: '',
            gender: '',
            salary: '',
            message : null,
        }
    }

    onChange = (e) =>{
        this.setState({
            //한줄로 모든 값 변환
            [e.target.name] : e.target.value
        });
    }

    render(){
        return(
            <div>
                <Typography variant="h4" style={style}>사용자 추가</Typography>
                <form>

                    <TextField 
                        type="text" 
                        id="userName" 
                        name="userName"
                        placeholder="userName을 입력하세요" 
                        label="사용자 이름"
                        margin="normal" 
                        fullWidth
                        value={this.state.userName}
                        onChange={this.onChange} />

                    <TextField 
                        type="password" 
                        id="password" 
                        name="password"
                        placeholder="password를 입력하세요" 
                        label="비밀번호 입력"
                        margin="normal" 
                        fullWidth
                        value={this.state.password}
                        onChange={this.onChange} />

                    <TextField 
                        type="text" 
                        id="fristName" 
                        name="fristName"
                        placeholder="fristName을 입력하세요" 
                        label="이름"
                        margin="normal" 
                        fullWidth
                        value={this.state.fristName}
                        onChange={this.onChange} />

                    <TextField 
                        type="text" 
                        id="lastName" 
                        name="lastName"
                        placeholder="lastName을 입력하세요" 
                        label="성"
                        margin="normal" 
                        fullWidth
                        value={this.state.lastName}
                        onChange={this.onChange} />

                    <Select 
                            label="성별"
                            id="gender"
                            name="gender"
                            fullWidth
                            value={this.state.gender}
                            onChange={this.onChange} >
                                <option aria-lable="None" value="">-------------선택------------</option>
                                <option value={'M'}>남자</option>
                                <option value={'F'}>여자</option>
                    </Select>

                    <TextField 
                        type="text" 
                        id="salary" 
                        name="salary"
                        placeholder="salary를 입력하세요" 
                        label="급여"
                        margin="normal" 
                        fullWidth
                        value={this.state.salary}
                        onChange={this.onChange} />
                </form>
            </div>
        );
    }
}
const style = {
    display : 'flex',
    justifyContent : 'center',
    marginTop : '20px',
  
  }
export default AddUser;
