import React, { Component } from 'react';
import ApiService from '../route/ApiService'; // ApiService.js

//테이블 관련
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';

//버튼
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

//icons
import CreateIcon from '@material-ui/icons/Create';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIon from '@material-ui/icons/Delete';

class UserList extends Component {
    constructor(props) {
        super(props);
        
        this.state = {
            users: [],
            message: null,
        }

        console.log('constructor run');
    }

    componentDidMount() {
        console.log('componentDidMount run');
        this.reloadUserList();
    }

    reloadUserList() {
        ApiService.fetchUsers()
            .then(res => {
            //debugger; data가 넘어 갔는 지 확인
                this.setState({ users: res.data });
            })
            .catch(err => {
                console.error('reloadUserList() 에러!', err);
            });

    }

    componentWillUnmount() {
        console.log('componentWillUnmount run');
    }

    render() {
        console.log('render run');
        console.log(this.state.users);
        return (
            <div>
                <Typography variant="h4" style={style}>사용자 리스트</Typography>
                <Button variant="contained" color="primary">추가</Button>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Id</TableCell>
                            <th>UserName</th>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>Gender</th>
                            <th>Salary</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </TableRow>
                    </TableHead>
                    <tbody>
                    {this.state.users.map(user => 
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.userName}</td>
                            <td>{user.firstName}</td>
                            <td>{user.lastName}</td>
                            <td>{user.gender}</td>
                            <td>{user.salary}</td>
                            <td>
                                <input type='button' value='수정'/>
                            </td>
                            <td>
                                <input type='button' value='삭제'/>
                            </td>
                        </tr>
                    )}
                    </tbody>
                </Table>
            </div>
        );
    }
}

const style = {
  display : 'flex',
  justifyContent : 'center',
}

export default UserList;
